import React from "react";
import { useParams } from "react-router-dom";
import { ADDTOCART } from "./COMPONENT/ROUTER/h";
import DATA from "./DataDetails";

const SinglePage = () => {

let {id}=useParams()


let nn=DATA.find((ss)=>ss.id ==Number(id))

  return (
    <div style={{width:600,border:"2px solid gray",margin:'100px auto',padding:10,backgroundColor:"lightblue" }}>
      <h1 style={{textAlign:'center',fontSize:45}}>{nn.id}</h1>
      <hr/>
      <img src={nn.image} style={{ height: 250, width: 320,marginLeft:150 }} />
      <br/>
      <h1 style={{textAlign:'center'}}>{nn.title}</h1>
      <br/>
      <br/>
      <div style={{lineHeight:1.6}}>
      <h2><span style={{fontWeight:900,color:"red"}}>PRIZE: </span>{nn.price}</h2>
      <h2><span style={{fontWeight:900,color:"red"}}>CATEGORY: </span>{nn.category}</h2>
      <h2><span style={{fontWeight:900,color:"red"}}>DESCRIPITION: </span>{nn.description}</h2>
     
      <h2><span style={{fontWeight:900,color:"red"}}>RATE: </span>{nn.rating.rate}</h2>
      <h2><span style={{fontWeight:900,color:"red"}}>COUNT: </span>{nn.rating.count}</h2>

      </div>
      <div style={{textAlign:'center'}}>
      <button onClick={() => ADDTOCART(nn)} style={{padding:"10px 25px"}}>ADD TO CART</button>
      </div>
    </div>
  );
};

export default SinglePage;
