import React, { useContext } from 'react'
// import DATA from './DataDetails';
// import CONTEXT from './Context';
import { useSelector, useDispatch } from "react-redux";
import { decrement, increment,remove } from "../STORE/Cartslice";


const CART = () => {

  let Names = useSelector((state) => state.cart);
  let Dispatch = useDispatch();

  let REMOVETOCART = (nnId) => {
    Dispatch(remove(nnId));
  };

  let INCREMENT = (nnId) => {
    dispatch(increment(nnId))
  }


  let DECREMENT = (nnId) => {
    dispatch(decrement(nnId))
  }

  return (
    <div
      style={{
        display: "grid",
        gridTemplateColumns: "250px 250px 250px 250px",
        justifyContent: "center",
        textAlign: "center",
        gap: 20,
        alignItem: "center",
      }}
    >

      {Names.length === 0 ? <h1>Your cart is empty</h1> : ""}
      {Names.map((nn) => {

        let FINAL = nn.quantity * nn.price
        return (
          <div
            style={{
              boxShadow:
                "rgba(0, 0, 0, 0.25) 0px 14px 28px, rgba(0, 0, 0, 0.22) 0px 10px 10px",
            }}
          >
            <h1>{nn.id}</h1>
            <br />
            <div style={{ lineHeight: 1.9 }}>
              <img src={nn.image} style={{ height: 110, width: 220 }} />
              {/* <p style={{height:50}}>{nn.title}</p> */}
              <p>{nn.title.slice(0, 18)}</p>

              <h2>{FINAL}</h2>

              <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', gap: 15, textAlign: 'center' }}>
                <button
                  style={{ width: "100%", padding: "10px " }}
                  onClick={() => DECREMENT(nn.id)}
                >
                  -
                </button>


                <h1>{nn.quantity}</h1>
                <button
                  style={{ width: "100%", padding: "10px " }}
                  onClick={() => INCREMENT(nn.id)}
                >
                  +
                </button>
              </div>
              {/* <h2>{nn.category}</h2> */}
        
              <button style={{ width: "100%", padding: "10px " }}
                onClick={() => REMOVETOCART(nn.id)}
              >
                Remove To Cart
              </button>
            </div>
          </div>
        );
      })}
    </div>
  )
}

export default CART
